mapper.select:

select 

from MST_CUST_TANKA
where Cust_Code = #{custCode,jdbcType=NCHAR}
  and Shop_Code = #{shopCode,jdbcType=NVARCHAR}
  and Item_Code = #{itemCode,jdbcType=NCHAR}
  and SALES_KB = #{salesKb,jdbcType=NCHAR}
  and Valid_To = #{validTo,jdbcType=NCHAR}

mapper.delete:

delete from MST_CUST_TANKA
where Cust_Code = #{custCode,jdbcType=NCHAR}
  and Shop_Code = #{shopCode,jdbcType=NVARCHAR}
  and Item_Code = #{itemCode,jdbcType=NCHAR}
  and SALES_KB = #{salesKb,jdbcType=NCHAR}
  and Valid_To = #{validTo,jdbcType=NCHAR}

mapper.update:

update MST_CUST_TANKA
set Valid_From = #{validFrom,jdbcType=NCHAR},
  Cust_Deli_Tanka = #{custDeliTanka,jdbcType=NUMERIC},
  Cust_Sell_Tanka = #{custSellTanka,jdbcType=NUMERIC},
  Cust_Bild_Tanka = #{custBildTanka,jdbcType=NUMERIC},
  Bunrui_Code = #{bunruiCode,jdbcType=NVARCHAR},
  STS_Code = #{stsCode,jdbcType=NCHAR},
  INS_USERID = #{insUserid,jdbcType=NCHAR},
  INS_PGID = #{insPgid,jdbcType=NVARCHAR},
  INS_YMD = #{insYmd,jdbcType=NCHAR},
  INS_TIME = #{insTime,jdbcType=NCHAR},
  UPD_USERID = #{updUserid,jdbcType=NCHAR},
  UPD_PGID = #{updPgid,jdbcType=NVARCHAR},
  UPD_YMD = #{updYmd,jdbcType=NCHAR},
  UPD_TIME = #{updTime,jdbcType=NCHAR}
where Cust_Code = #{custCode,jdbcType=NCHAR}
  and Shop_Code = #{shopCode,jdbcType=NVARCHAR}
  and Item_Code = #{itemCode,jdbcType=NCHAR}
  and SALES_KB = #{salesKb,jdbcType=NCHAR}
  and Valid_To = #{validTo,jdbcType=NCHAR}
