mapper.select:

select 

from MST_Func_Auth
where Auth_Code = #{authCode,jdbcType=NCHAR}
  and Func_Kb = #{funcKb,jdbcType=NCHAR}
  and Func_Id = #{funcId,jdbcType=NVARCHAR}

mapper.delete:

delete from MST_Func_Auth
where Auth_Code = #{authCode,jdbcType=NCHAR}
  and Func_Kb = #{funcKb,jdbcType=NCHAR}
  and Func_Id = #{funcId,jdbcType=NVARCHAR}
