mapper.select:

select 

from TBL_SEI01_URK_MSH_HEAD
where Jigyo_Code = #{jigyoCode,jdbcType=NCHAR}
  and Chain_Code = #{chainCode,jdbcType=NUMERIC}
  and Chain_Idx = #{chainIdx,jdbcType=NUMERIC}
  and Cust_Code = #{custCode,jdbcType=NCHAR}
  and Shop_Code = #{shopCode,jdbcType=NVARCHAR}
  and Account_Month = #{accountMonth,jdbcType=NCHAR}

mapper.delete:

delete from TBL_SEI01_URK_MSH_HEAD
where Jigyo_Code = #{jigyoCode,jdbcType=NCHAR}
  and Chain_Code = #{chainCode,jdbcType=NUMERIC}
  and Chain_Idx = #{chainIdx,jdbcType=NUMERIC}
  and Cust_Code = #{custCode,jdbcType=NCHAR}
  and Shop_Code = #{shopCode,jdbcType=NVARCHAR}
  and Account_Month = #{accountMonth,jdbcType=NCHAR}
