mapper.select:
SELECT JGY.JGYCD, JGY.JGYMEI FROM MST_S_JIGYO JGY
WHERE
JGY.DELFLG = '0'
AND JGY.STRYMD <= #{businessDate}
AND JGY.ENDYMD >= #{businessDate}
ORDER BY JGY.JGYCD

SELECT ${Search_Max} MAX(CRK.St_Kb)ã€€as stKb, MAX(CSJ.Jigyo_Code) as jigyoCode, CRK.Cust_Code as custCode, MAX(CST.Cust_Nm_R) as custNmR, CRK.Correct_Kb as correctKb, MAX(CRK.Correct_Cause) as correctCause, MAX(CRK.Zero_Dlvdat_Flg) as zeroDlvdatFlg, MAX(CRK.Bikou) as biKou, MAX(CRK.STS_Code) as stsCode FROM MST_CORRECT_KB CRK LEFT JOIN MST_CUSTOMER CST ON (CRK.Cust_Code = CST.Cust_Code) LEFT JOIN MST_CUSTJGYO CSJ ON (CRK.Cust_Code = CSJ.Cust_Code) WHERE 1=1
AND CSJ.Jigyo_Code = #{JigyoshoCode}
AND CRK.Cust_Code = #{CustCode}
AND CST.Cust_Nm LIKE #{CustNmR}
AND CRK.Correct_Kb = #{CorrectKb}
AND CRK.Zero_Dlvdat_Flg = #{ZeroDlvdatFlg}
AND CRK.STS_Code = '1'
AND CRK.St_Kb = '2'
GROUP BY CRK.Cust_Code, CRK.Correct_Kb ORDER BY CRK.Cust_Code

SELECT CST.Cust_Nm_R as CustNm, CSJ.Jigyo_Code as JigyoCode
FROM MST_CUSTOMER CST
LEFT JOIN MST_CUSTJGYO CSJ ON (CST.Cust_Code = CSJ.Cust_Code)
WHERE CST.Cust_Code = #{CustCode}
AND CSJ.Jigyo_Code = #{JigyoCode}
